msg.device=[];
if (msg.cmd.type === "lights") {
    if (msg.cmd.value !== undefined) {
        for (var i = 0; i < msg.items.length; i++) {
            if (msg.items[i].type === "dimmers" && msg.cmd.cmd !== "off") {
               var val = msg.cmd.value/100 * msg.items[i].setBrightness.valueMax;
               msg.device[i] = msg.items[i].setBrightness;
               msg.device[i].value = val;
            }else if (msg.cmd.value > 0) {
                msg.device[i] = msg.items[i].setOn;
            }else {
                msg.device[i] = msg.items[i].setOff;
            }
        }
    }else if (msg.cmd.cmd === "on") {
        for (var i = 0; i < msg.items.length; i++) {
            msg.device[i] = msg.items[i].setOn;
        }
    }else if (msg.cmd.cmd === "off") {
        for (var i = 0; i < msg.items.length; i++) {
            msg.device[i] = msg.items[i].setOff;
        }
    }
}else if (msg.cmd.type === "aux" || msg.cmd.type === "scenes") {
    if (msg.cmd.cmd === "on" || msg.cmd.cmd === "set") {
        if (msg.cmd.value !== undefined) {
          for (var i = 0; i < msg.items.length; i++) {
            if (msg.items[i].setBrightness !== undefined) {
                var val = msg.cmd.value;
                val = precisionRound(val, 1);
                msg.device[i] = msg.items[i].setBrightness;
                msg.device[i].value = val;
            } 
            else if (msg.cmd.value > 0) {
               msg.device[i] = msg.items[i].setOn;
            }
            else {
               msg.device[i] = msg.items[i].setOff;
            }
        }
        }else {
            for (var i = 0; i < msg.items.length; i++) {
                msg.device[i] = msg.items[i].setOn;
                msg.device[i] = Object.assign({}, msg.device[i], { eep: msg.items[i].eep });
            }
        }
    }else if (msg.cmd.cmd === "off") {
        for (var i = 0; i < msg.items.length; i++) {
            if (msg.items[i].setOff !== undefined) {
                msg.device[i] = msg.items[i].setOff;
            }
        }
    }
}  

