         if (msg.cmd.value !== undefined) {
            var val = msg.cmd.value / 100 * msg.items[i].setBrightness.valueMax;
            val = precisionRound(val, 1);
            var newVal = parseInt(msg.current[i].Data[0].Value) - val;
            if (newVal < msg.items[i].setBrightness.valueMin) {
               newVal = msg.items[i].setBrightness.valueMin;
            }
            newVal = precisionRound(newVal, 1);
            msg.device[cptok] = msg.items[i].setBrightness;
            msg.device[cptok].value = newVal;
            cptok++;
         } else {
            var newVal = parseInt(msg.current[i].Data[0].Value) - (0.2 * msg.items[i].setBrightness.valueMax);
            if (newVal < msg.items[i].setBrightness.valueMin) {
               newVal = msg.items[i].setBrightness.valueMin;
            }
            newVal = precisionRound(newVal, 1);
            msg.device[cptok] = msg.items[i].setBrightness;
            msg.device[cptok].value = newVal;
            cptok++;
         }
      }
   }
}

