var replaceAragonOnly = flow.get("replaceAragonOnly"); // See optional settings
if (replaceAragonOnly === undefined) {
    replaceAragonOnly = false;
}
var addLeadingNumbers = flow.get("addLeadingNumbers"); // See optional settings
if (addLeadingNumbers === undefined) {
    addLeadingNumbers = true;
}
node.warn("addLeadingNumbers: "+addLeadingNumbers);

var module = global.get("module");
var devices = module.settingsGetValue("msgActiveDeviceList");
if (devices === undefined) {
    devices = module.settingsGetValue("msgDeviceList");
    node.warn("Using unfiltered msgDeviceList");
}

// Ensure there's an accessories array
if (!msg.payload.accessories) {
    msg.payload.accessories = [];
}

node.warn("Number of Homebridge accessories before: " + msg.payload.accessories.length);

if(replaceAragonOnly){
    msg.payload.accessories = msg.payload.accessories.filter(item => !(item.aragon));
} else {
    msg.payload.accessories = msg.payload.accessories.filter(item => !(item.accessory = "mqttthing"));
}

var zoneNumbers = {};
var currentNumber = 1;

// Enumerate devices and create accessories for dimmers
devices.forEach(function(device) {
 // If the zone is not in zoneNumbers, add it and initialize its number to 1
    if (!zoneNumbers.hasOwnProperty(device.zone)) {
        zoneNumbers[device.zone] = currentNumber++;
    }

    // Get the unique number for the zone and increment it for the next device in the same zone
    var zoneNumber = zoneNumbers[device.zone];

    // Pad the zoneNumber with a leading zero if it's less than 10
    var paddedZoneNumber = "";
    if(addLeadingNumbers){
        paddedZoneNumber = zoneNumber.toString().padStart(2, '0') + " ";
    }

    // Add the paddedZoneNumber at the beginning of the id string
    var id = paddedZoneNumber + device.zone + " - " + device.object;

    var data = { 
        "aragon": { 
            "type": device.type,
            "object": device.object,
            "zone": device.zone
        }
    };

    if (device.type === "dimmers") {
        let newAccessory = {
            "type": "lightbulb-Dimmable",
            "name": id,
            "debounceRecvms": 2000,
            "optimizePublishing": true,
            "topics": {
                //"getOnline": "aragon/getOnline",
                "getOn": "aragon/getOn/" + JSON.stringify(data),
                "setOn": "aragon/setOn/" + JSON.stringify(data),
                "getBrightness": "aragon/getBrightness/" + JSON.stringify(data),
                "setBrightness": "aragon/setBrightness/" + JSON.stringify(data)
            },
            "aragon": {
               "type": device.type,
               "object": device.object,
               "zone": device.zone
            },
            "integerValue": false,
            "onValue": "on",
            "offValue": "off",            
            "manufacturer": "Aragon",
            "accessory": "mqttthing"
        };
        // Add the new accessory to the payload
        msg.payload.accessories.push(newAccessory);
    }
    else if (device.type === "lights") {
        let newAccessory = {
            "type": "lightbulb",
            "name": id,
            "debounceRecvms": 2000,
            "optimizePublishing": true,
            "topics": {
                //"getOnline": "aragon/getOnline",
                "getOn": "aragon/getOn/" + JSON.stringify(data),
                "setOn": "aragon/setOn/" + JSON.stringify(data)
            },
            "aragon": {
                "type": device.type,
                "object": device.object,
                "zone": device.zone
            },
            "integerValue": false,
            "onValue": "on",
            "offValue": "off",            
            "manufacturer": "Aragon",
            "accessory": "mqttthing"
        };
        // Add the new accessory to the payload
        msg.payload.accessories.push(newAccessory);
    } else if (device.type === "blinds") {
        let newAccessory = {
            "type": "windowCovering",
            "name": id,
            "debounceRecvms": 2000,
            "optimizePublishing": true,
            "topics": {
                "setTargetPosition": "aragon/setTargetPosition/" + JSON.stringify(data),
                "getCurrentPosition": "aragon/getCurrentPosition/" + JSON.stringify(data)
            },
            "aragon": {
                "type": device.type,
                "object": device.object,
                "zone": device.zone
            },
            "integerValue": false,
            "manufacturer": "Aragon",
            "accessory": "mqttthing"
        };
        // Add the new accessory to the payload
        msg.payload.accessories.push(newAccessory);
    } else if (device.type === "scenes") {
        let newAccessory = {
            "type": "switch",
            "name": id,
            "debounceRecvms": 2000,
            "optimizePublishing": true,
            "topics": {
                //"getOnline": "aragon/getOnline",
                //"getOn": "aragon/getOn/" + JSON.stringify(data),
                "setOn": "aragon/setOn/" + JSON.stringify(data)
            },
            "aragon": {
                "type": device.type,
                "object": device.object,
                "zone": device.zone
            },
            "turnOffAfterms": 1000,
            "integerValue": false,
            "onValue": "on",
            "offValue": "off",              
            "manufacturer": "Aragon",
            "accessory": "mqttthing"
        };
        // Add the new accessory to the payload
        msg.payload.accessories.push(newAccessory);
    }
});

node.warn("Number of Homebridge accessories after: " + msg.payload.accessories.length);

return msg;

var module = global.get("module");
var allRooms = module.settingsGetValue("allRooms");
var language = module.settingsGetValue("msgLang");

var topicParts = msg.topic.split("/");
var subTopic = topicParts[1]; // This will be 'setOn', 'setBrightness' etc
var dataString = topicParts.slice(2).join("/"); // Reconstruct the data part of the topic
var data = JSON.parse(dataString); // Parse the JSON string to an object

// Now 'data' contains the object with the 'aragon' property
var type = data.aragon.type;
var object = data.aragon.object;
var zone = data.aragon.zone;
var text = "";
var value = msg.payload;
var msgHB = null;

// Function to check if zone exists in allRooms, and assign first siteName to zone if not
function checkAndAssignSiteName(allRooms, zone) {
  // Check if any object in allRooms has a siteName equal to zone
  const siteExists = allRooms.find(adapter => adapter.siteName === zone);

  // If zone doesn't exist in allRooms, assign zone to Default
  if (!siteExists) {
    zone = 'Default';
  }
  return zone;
}

function limitPercentValue(val){
    let intValue = parseInt(val, 10);
    if (isNaN(intValue) || intValue < 1 || intValue > 100) {
        intValue = 1;
    }
    else if (intValue > 100) {
        intValue = 100;
    }
    return intValue.toString();
}

//node.warn("type: " + type + " subTopic: " + subTopic + " value: " + value + " object: " + object + " zone: " + zone );

switch(type){
    case "dimmers":
        switch(subTopic){
            case "setBrightness": 
                if (language === "fr"){
                    text = "Règle la luminosité de " + object + " dans la " + zone + " sur " + limitPercentValue(value) + " pourcents";
                } else if (language === "de"){
                    text = zone + " " + object + " die helligkeit auf " + limitPercentValue(value) + " prozent einstellen";
                } else{
                    text = "Set the " + zone + " " + object + " brightness to " + limitPercentValue(value) + " percent";
                }                
                break;
            case "setOn":
                if (language === "fr") {
                    const translate = word => word === 'on' ? 'allume la ' : word === 'off' ? 'éteins la ' : word;
                    text = translate(value) + object + " dans la " + zone;
                } else if (language === "de") {
                    const translate = word => word === 'on' ? 'ein' : word === 'off' ? 'aus' : word;
                    text = zone + " " + object + " " + translate(value);
                } else {
                    text = zone + " " + object + " " + value;
                }
                break;
            default:
                node.warn("Unhandled action in 'dimmers'");
                node.warn("type: " + type + " subTopic: " + subTopic + " value: " + value + " object: " + object + " zone: " + zone );
                break;
        }
        break;
    case "lights":
        switch(subTopic){
            case "setOn":
                if (language === "fr") {
                    const translate = word => word === 'on' ? 'allume la ' : word === 'off' ? 'éteins la ' : word;
                    text = translate(value) + object + " dans la " + zone;
                } else if (language === "de") {
                    const translate = word => word === 'on' ? 'ein' : word === 'off' ? 'aus' : word;
                    text = zone + " " + object + " " + translate(value);
                } else {
                    text = zone + " " + object + " " + value;
                }
                break;
            default:
                node.warn("Unhandled action in 'lights' " + subTopic);
                break;
        }
        break;
    case "blinds":
        switch(subTopic){
            case "setTargetPosition": 
                if (language === "fr") {
                    text = "Ouvre la " + object + " dans la " + zone + " sur " + limitPercentValue(value) + " pourcents";
                } else if (language === "de") {
                    text = "Set the " + zone + " " + object + " to " + limitPercentValue(value) + " percent";
                } else {
                    text = "Set the " + zone + " " + object + " to " + limitPercentValue(value) + " percent";
                }            

                // Prepare fake notification:
                msgHB = {};
                msgHB.topic = msg.topic.replace("setTargetPosition", "getCurrentPosition");
                msgHB.payload = msg.payload;
                break;
            case "getCurrentPosition":
                ; // fake reply in setTargetPosition
                break;
            default:
                node.warn("Unhandled action in 'blinds' " + subTopic);
                break;
        }
        break;
    case "scenes":
        switch(subTopic){
            case "setOn":
                if(value.toLowerCase() === 'on'){
                    if (language === "fr") {
                        text = "Active " + object + " dans la " + zone;
                    } else if (language === "de") {
                        text = "Aktiviren " + object + " im " + zone;
                    } else {
                        text = "Activate " + zone + " " + object;
                    }
                }
                break;
            default:
                node.warn("Unhandled action in 'scenes' " + subTopic);
                break;
        }
        break;
}
if(text===''){
    return;
}

//node.warn("To NLU: "+text);
var msgNLU = {};
msgNLU.payload = {};
msgNLU.payload.input = text;
msgNLU.payload.siteId = checkAndAssignSiteName(allRooms, zone);

return [msgNLU, msgHB];

// If you add or edit Mqttthing devices from the Homebridge plugin, they will loose their Aragon settings. When you add Aragon
// all mqttthings will be replaced. To replace only Aragon devices, set replaceAragonOnly to true, save and add devices
// Default: false 
flow.set("replaceAragonOnly", false); 

// When the accesories have a leading number in their name, they can be automatically sorted into rooms by the realKNX iOS application
// With the added leading number comes the risk off mane truncation in the Homebridge Dashboard
// Default: true 
flow.set("addLeadingNumbers", false);
