var module = global.get("module");
let language = module.settingsGetValue("msgLang");


/************** FALL DETECTION ****************/
switch (language) {
    case "de":
        flow.set("fall_query", "Wichtige Nachricht! Es sieht so aus, als wären Sie gestürzt, ich solle um Hilfe rufen?");
        break;
    case "fr":
        flow.set("fall_query", "Message important! il semblerait que vous soyez tombé, dois-je appeler de l'aide ?");
        break;
    default:
        flow.set("fall_query", "Important message! it seems like you have fallen, shall I call for help?");
}


/************** ROOM IN ***********************/
flow.set("room_in_dialog", null); // Say something, don't start a voice dialog
switch (language) {
    case "de":
        flow.set("room_in_action", ["das Licht im Schlafzimmer einschalten","das Licht im Badezimmer einschalten"]);
        flow.set("room_in_say", "Willkommen");
        break;
    case "fr":
        flow.set("room_in_action", ["allumer les lumières de la chambre", "allumer les lumières de la salle de bain"]);
